import React, { useEffect, useState } from 'react';
import axios from 'axios';

const apiKey = '1a3d76623f9ad51e2c3ae7f2409329a9';

export default function Home(props) {
	const [city, setCity] = useState('');
	const [weather, setWeather] = useState({});

	function queryWeatherAPI(queryCity) {
		axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${queryCity}&APPID=${apiKey}`)
		.then(function(response) {
			console.log('response', response);
			setWeather(response);
			return response;
		})
		.catch(function(error) {
			console.log('error', error);
			return error;		
		});
	}

	useEffect(() => {
		//setCity('London');
		//queryWeatherAPI('London');
		//console.log('weather test', queryWeatherAPI('London'));
		const urlParams = new URLSearchParams(props.location.search)
		const cityParam = urlParams.get('city') ? urlParams.get('city') : 'London';
		setCity(cityParam);
		console.log('weather test', queryWeatherAPI(cityParam));
	}, []);

	console.log('weather', weather);

	return (
		<div className="weatherInfo">
			<div className="cities">
				<a href="/?city=Rio de Janeiro">Rio de Janeiro</a>
				<a href="/?city=Paris">Paris</a>
				<a href="/?city=Rome">Rome</a>
				<a href="/?city=Brussels">Brussels</a>
				<a href="/?city=London">London</a>
			</div>

			<h1 className="city">Weather in {city}</h1>
			<p className="description">Description: {weather.data && weather.data.weather[0].description}</p>
			<p className="temp">Current Temperature: {weather.data && weather.data.main.temp} Kelvin</p>
			<p>Today's High Temperature: {weather.data && weather.data.main.temp_max} Kelvin</p>
			<p>Today's Low Temperature: {weather.data && weather.data.main.temp_min} Kelvin</p>
			<p className="humidity">Humidity: {weather.data && weather.data.main.humidity}</p>
			<p>Cloudy: {weather.data && weather.data.clouds.all}</p>
			<p>Wind: {weather.data && weather.data.wind.speed}km/H coming at {weather.data && weather.data.wind.direction}</p>
		</div>
	);
}

